<div class="card my-1 mx-5 quizCard h-80">
	<div class="btn formBtn returnBtn"><i class="fas fa-chevron-circle-left"></i></div>
	<div class="card-header">
		<div class="card-img-top" style="background-image:url('./data/image/default.dat');"></div>
		<h5 class="card-title quizName" style="background-color:var(--cI)"></h5>
		<h6 class="card-subtitle quizUsername" style="color:var(--cI)"></h6>
	</div>
	<div class="card-body my-1 mx-3">
		<p class="quizDesc"></p>
	</div>
	<hr>
	<div class="card-tags quizTags mx-3 p-1 h-20">
		<!-- <span class="card-tag"><i class="fab fa-slack-hash"></i>hashtag</span> -->
	</div>
</div>
<div class="quizMenuControl my-4 mx-5 p-1 h-10">
	<div class="btn formBtn quizMenuBtn thiccBtn" id="quizMenuPlay">
		<i class="fas fa-play mx-2"></i><span class="thiccBtnText font-xs-100 font-md-0">Play</span>
	</div>
	<div class="btn formBtn quizMenuBtn thiccBtn" id="quizMenuEdit">
		<i class="fas fa-pen-square mx-2"></i><span class="thiccBtnText font-xs-100 font-md-0">Edit</span>
	</div>
	<div class="btn formBtn quizMenuBtn thiccBtn" id="quizMenuDelete">
		<i class="fas fa-trash mx-2"></i><span class="thiccBtnText font-xs-100 font-md-0">Delete</span>
	</div>
	<div class="btn formBtn quizMenuBtn thiccBtn dontShow" id="quizMenuSave">
		<i class="fas fa-bookmark mx-2"></i><span class="thiccBtnText font-xs-100 font-md-0">Save to List</span>
	</div>
	<div class="btn formBtn quizMenuBtn thiccBtn dontShow" id="quizMenuPreview">
		<i class="fas fa-play-circle mx-2"></i><span class="thiccBtnText font-xs-100 font-md-0">Preview Song</span>
	</div>
</div>