<form class="userEditForm h-90 w-100">
	<div class="card my-1 mx-5 quizCard h-100">
		<div class="card-header my-0">
			<div class="card-img-top dropSite dropImage quizFormImage userImage" style="background-image:url('./data/image/default.dat');">
				<div class="c0-bg p-2 opacity-75"><i class="fas fa-plus-square mx-2"></i><span>Drag and drop image or Click to select image</span></div>
				<button class="btn formBtn removeFileBtn" id="dropBtn">Remove</button>
			</div>
			<input type="file" name="dropImageFile" class="dropImageFile quizFormImageFile dontShow"/>
			
		</div>
		<input type="text" class="card-title userUsername inputBox w-100 px-4" placeholder="UserName here" maxlength="30"/>
		<div class="card-body my-0 mx-3 w-80">
			<input type="text" class="card-subtitle userFirstname inputBox w-100 px-4" placeholder="First Name here" maxlength="30"/>
			<input type="text" class="card-subtitle userLastname inputBox w-100 px-4" placeholder="Last Name here" maxlength="30"/>
			<label class="">DOB: <input type="date" class="userDOB inputBox w-100 px-4"/></label>
			
			<label class="">Email: <input type="email" class="userEmail inputBox w-100 px-4" maxlength="40"/></label>
			<textarea class="userDesc formTextarea w-100 px-3 h-30" placeholder="About me" maxlength="100"></textarea>
		</div>
		<div class="card-body my-0 mx-3 w-70">
			<h6 class="font-70">Pick a Shape and Color</h6>
			<div class="row h-30 mx-3">
				<div class="my-1 col-2 formRadioContainer h-100" data-user-color="A">
					<input type="radio" class="formRadioBtn" name="userColor" id="userColorA" value="A">
					<div class="formRadioBox userColorRadioChoice cA-bd" id="userColChoiceA"></div>
				</div>
				<div class="my-1 col-2 formRadioContainer h-100" data-user-color="B">
					<input type="radio" class="formRadioBtn" name="userColor" id="userColorB" value="B">
					<div class="formRadioBox userColorRadioChoice cB-bd" id="userColChoiceB"></div>
				</div>
				<div class="my-1 col-2 formRadioContainer h-100" data-user-color="C">
					<input type="radio" class="formRadioBtn" name="userColor" id="userColorC" value="C">
					<div class="formRadioBox userColorRadioChoice cC-bd" id="userColChoiceC"></div>
				</div>
				<div class="my-1 col-2 formRadioContainer h-100" data-user-color="D">
					<input type="radio" class="formRadioBtn" name="userColor" id="userColorD" value="D">
					<div class="formRadioBox userColorRadioChoice cD-bd" id="userColChoiceD"></div>
				</div>
				<div class="my-1 col-2 formRadioContainer h-100" data-user-color="E">
					<input type="radio" class="formRadioBtn" name="userColor" id="userColorE" value="E">
					<div class="formRadioBox userColorRadioChoice cE-bd" id="userColChoiceE"></div>
				</div>
				<div class="my-1 col-2 formRadioContainer h-100" data-user-color="F">
					<input type="radio" class="formRadioBtn" name="userColor" id="userColorF" value="F">
					<div class="formRadioBox userColorRadioChoice cF-bd" id="userColChoiceF"></div>
				</div>
			</div>
			<div class="row h-30 mx-3">
				<div class="my-1 col-2 formRadioContainer h-100" data-user-color="A">
					<input type="radio" class="formRadioBtn" name="userShape" id="userShapeA" value="A">
					<i class="fas fa-square formRadioBox userShapeRadioChoice" id="userShapeChoiceA"></i>
				</div>
				<div class="my-1 col-2 formRadioContainer h-100" data-user-color="B">
					<input type="radio" class="formRadioBtn" name="userShape" id="userShapeB" value="B">
					<i class="fas fa-circle formRadioBox userShapeRadioChoice" id="userShapeChoiceB"></i>
				</div>
				<div class="my-1 col-2 formRadioContainer h-100" data-user-color="C">
					<input type="radio" class="formRadioBtn" name="userShape" id="userShapeC" value="C">
					<i class="fas fa-caret-right formRadioBox userShapeRadioChoice font-160" id="userShapeChoiceC"></i>
				</div>
				<div class="my-1 col-2 formRadioContainer h-100" data-user-color="D">
					<input type="radio" class="formRadioBtn" name="userShape" id="userShapeD" value="D">
					<i class="fas fa-envelope-open formRadioBox userShapeRadioChoice" id="userShapeChoiceD"></i>
				</div>
				<div class="my-1 col-2 formRadioContainer h-100" data-user-color="E">
					<input type="radio" class="formRadioBtn" name="userShape" id="userShapeE" value="E">
					<i class="fas fa-star formRadioBox userShapeRadioChoice" id="userShapeChoiceE"></i>
				</div>
				<div class="my-1 col-2 formRadioContainer h-100" data-user-color="F">
					<input type="radio" class="formRadioBtn" name="userShape" id="userShapeF" value="F">
					<i class="fas fa-heart formRadioBox userShapeRadioChoice" id="userShapeChoiceF"></i>
				</div>
			</div>
		</div>
	</div>
</form>
<div class="quizMenuControl my-2 mx-5 p-1 h-5">
	<div class="btn formBtn userEditBtn mx-0 thiccBtn" id="userEditBtnSaveUser">
		<i class="fas fa-save mx-2"></i><span class="userEditBtnText font-xs-100 font-md-0 thiccBtnText">Save Profile</span>
	</div>
	<div class="btn formBtn userEditBtn mx-0 thiccBtn" id="userEditBtnCancelUserEdit">
		<i class="fas fa-window-close mx-2"></i><span class="userEditBtnText font-xs-100 font-md-0 thiccBtnText">Cancel Profile Edit</span>
	</div>
</div>